('7', '19', 'les fils de Bigwaï : 2 067 ;', '16'),
('7', '20', 'les fils d’Adine : 655 ;', '16'),
('7', '21', 'les fils d’Ater, c’est-à-dire de Hizqiya : 98 ;', '16'),
('7', '22', 'les fils de Hashoum : 328 ;', '16'),
('7', '23', 'les fils de Béçaï : 324 ;', '16'),
